import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import BabyHeading from '../Heading'
import { getProductsData } from '../../../app/data/productsData'
import ProductCard from '../../Common/Product/ProductCard'

const TrendingProducts = () => {
    const [trendingProducts, setTrendingProducts] = useState([])

    useEffect(() => {
        // En çok yorumlanan 100 ürünü al ve random 20 tanesini seç
        const allProducts = getProductsData()
        
        // Yorum sayısına göre sırala (en yüksekten en düşüğe)
        const sortedByReviews = allProducts.sort((a, b) => b.reviewCount - a.reviewCount)
        
        // En çok yorumlanan 100 ürünü al
        const top100Products = sortedByReviews.slice(0, 100)
        
        // Random 20 ürün seç
        const shuffled = [...top100Products].sort(() => 0.5 - Math.random())
        const random20 = shuffled.slice(0, 20)
        
        setTrendingProducts(random20)
    }, [])

    return (
        <>
            <section id="baby_trending_product" className="ptb-100">
                <div className="container">
                    <BabyHeading heading="Popüler Ürünler" />
                    <div className="row">
                        {trendingProducts.map((product, index) => (
                            <div className="col-lg-3 col-md-4 col-sm-6" key={product.id}>
                                <ProductCard data={product} />
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </>
    )
}

export default TrendingProducts
