import React from 'react'

const Loader = () => {
    return (
        <>
            <div className="loader_wrapper">
                <img 
                    width="64px" 
                    height="64px"
                    src="/favicon.ico" 
                    alt="Malikane Electronics" 
                    style={{
                        animation: 'spin 2s linear infinite',
                        borderRadius: '50%',
                        boxShadow: '0 4px 15px rgba(0, 123, 255, 0.3)'
                    }}
                />
            </div>
        </>
    )
}

export default Loader
