import React, { useState, useEffect } from 'react'
import banner1 from '../../../assets/img/banner1.png'
import banner2 from '../../../assets/img/banner2.png'
import banner3 from '../../../assets/img/banner3.png'
import banner4 from '../../../assets/img/banner4.png'
import banner5 from '../../../assets/img/banner5.png'

const Slider = () => {
    const [currentSlide, setCurrentSlide] = useState(0)

    // Gerçek banner resimleri
    const slides = [
        {
            id: 1,
            image: banner1,
            title: 'Elektronik Ürünlerde Büyük İndirim',
            subtitle: 'En kaliteli ürünler, en uygun fiyatlarla',
            buttonText: 'Alışverişe Başla',
            buttonLink: '/shop'
        },
        {
            id: 2,
            image: banner2,
            title: 'Yeni Gelen Ürünler',
            subtitle: 'Son teknoloji ürünleri keşfedin',
            buttonText: 'Keşfet',
            buttonLink: '/shop'
        },
        {
            id: 3,
            image: banner3,
            title: 'Kampanya Fırsatları',
            subtitle: 'Sınırlı süreli özel fiyatlar',
            buttonText: 'Fırsatları Gör',
            buttonLink: '/shop'
        },
        {
            id: 4,
            image: banner4,
            title: 'Premium Kalite',
            subtitle: 'Sadece en iyi markalar',
            buttonText: 'İncele',
            buttonLink: '/shop'
        },
        {
            id: 5,
            image: banner5,
            title: 'Hızlı Teslimat',
            subtitle: 'Aynı gün kargo imkanı',
            buttonText: 'Sipariş Ver',
            buttonLink: '/shop'
        }
    ]

    // Otomatik slider
    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentSlide((prev) => (prev + 1) % slides.length)
        }, 5000) // 5 saniyede bir değişir

        return () => clearInterval(timer)
    }, [slides.length])

    const goToSlide = (index) => {
        setCurrentSlide(index)
    }

    const goToPrevious = () => {
        setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length)
    }

    const goToNext = () => {
        setCurrentSlide((prev) => (prev + 1) % slides.length)
    }

    return (
        <section className="hero-slider">
            <div className="slider-container">
                <div className="slider-wrapper">
                    {slides.map((slide, index) => (
                        <div
                            key={slide.id}
                            className={`slide ${index === currentSlide ? 'active' : ''}`}
                            style={{
                                backgroundImage: `url(${slide.image})`,
                                backgroundSize: 'cover',
                                backgroundPosition: 'center'
                            }}
                        >
                            <div className="slide-content">
                                <div className="container">
                                    <div className="row">
                                        <div className="col-lg-6">
                                            <div className="slide-text">
                                                <h1 className="slide-title">{slide.title}</h1>
                                                <p className="slide-subtitle">{slide.subtitle}</p>
                                                <a href={slide.buttonLink} className="btn btn-primary btn-lg">
                                                    {slide.buttonText}
                                                </a>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Navigation arrows */}
                <button className="slider-nav prev" onClick={goToPrevious}>
                    <i className="fa fa-chevron-left"></i>
                </button>
                <button className="slider-nav next" onClick={goToNext}>
                    <i className="fa fa-chevron-right"></i>
                </button>

                {/* Dots indicator */}
                <div className="slider-dots">
                    {slides.map((_, index) => (
                        <button
                            key={index}
                            className={`dot ${index === currentSlide ? 'active' : ''}`}
                            onClick={() => goToSlide(index)}
                        />
                    ))}
                </div>
            </div>
        </section>
    )
}

export default Slider
